import React from "react";
import Slider from "react-slick";
import "./Testimonia.css";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { Avatar } from "@material-ui/core";

const Testimonial = () => {
  return (
    <div
      className="Testimonial"
      style={{
        display: "flex",
        justifyContent: "center",
        marginTop: 50,
      }}
    >
      <div style={{ width: "50%" }}>
        <Slider>
          <Card img="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSaXrFMnQrS3cdGFTB-UpG-5qMGMQyybPu7xg&usqp=CAU" />
          <Card img="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSRK_-7RH5-LwJ4MKlyHKcPQh5--rWhCXucog&usqp=CAU" />
          <Card img="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSL8wj2ko4LEjsgdk7RjW7wGHPq6QTekGHkOg&usqp=CAU" />
        </Slider>
      </div>
    </div>
  );
};

const Card = ({ img }) => {
  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        textAlign: "center",
        alignItems: "center",
        color: "gray",
      }}
    >
      <Avatar
        imgProps={{ style: { borderRadius: "50%" } }}
        src={img}
        style={{
          width: 120,
          height: 120,
          border: "1px solid lightgray",
          padding: 7,
        }}
      />
      <p>
        Lorem Ipsum is simply dummy text of the printing and typesetting
        industry. Lorem Ipsum has been the industry's standard dummy text ever
        since the 1500s, when an unknown printer took a galley of type and
        scrambled it to make a type specimen book. It has survived not only five
        centuries, but also the leap into electronic typesetting, remaining
        essentially unchanged. It was popularised in the 1960s with the release
        of Letraset sheets containing Lorem Ipsum passages, and more recently
        with desktop publishing software like Aldus PageMaker including versions
        of Lorem Ipsum.
      </p>
      <p style={{ fontStyle: "italic", marginTop: 20 }}>
        <span style={{ fontWeight: 500, color: "green" }}>PAULA WILSON</span>,
        Media Analyst
      </p>
    </div>
  );
};

export default Testimonial;
